package PieToast;

public class explode {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 1;
		while(1<2){
			for(int y=0; y<x; y++){
				System.out.print(" ");
			}
			System.out.print("o");
			for(int z=0; z<10; z++){
				System.out.println();
			}
			x++;
		}
	}
}
